/* Show all modules */

path = "./modules";

modules = [];

fs = require("fs");

fs.readdir(path, function(err, files) {
  if (err) {
    throw err;
  }
  modules = files;
});

list = function(boxid) {
  var content, module, _i, _len;
  content = "<ul>";
  for (_i = 0, _len = modules.length; _i < _len; _i++) {
    module = modules[_i];
    console.log(module);
    content += "<li><a class='module_single' href='#' name='" + module + "'>" + module + "</a></li>";
  }
  content += "</ul>";
  $(boxid).html(content);
  $(".module_single").click(function() {
    get_config($(this).attr("name"));
  });
};

get_config = function(modname) {
  var moddir;
  moddir = path + "/" + modname + "/";
  fs.readFile(moddir + "config.json", "utf8", function(err, data) {
    var mod;
    if (err) {
      console.log("Error: " + err);
      return;
    }
    data = JSON.parse(data);
    mod = require(moddir + data.hook.slice(0, -3));
    mod("#box", {});
  });
};
